package org.example.employee;/* Занимается разработкой и реализацией маркетинговых стратегий, рекламой, продвижением продукции или услуг. */public class MarketingDepartment extends Employee{    private String marketingStrategy;    private String workCar;    public void developsStrategy() {        System.out.println("Работник " + name + " разробатывает стратегию под названием " + marketingStrategy );    }    public void drive() {        System.out.println("Работник " + name + " едит на машине " + workCar);    }}