package org.example.employee;/* Отвечает за производственные процессы, изготовление продукции или предоставление услуг. */public class ProductionDepartment extends Employee{    private String machine;    private String detail;    public void drills() {        System.out.println("Работник " + name + " сверлит за станком " + machine);    }    public void makesPart() {        System.out.println("Работник " + name + " изготавливает деталь " + detail);    }}