package org.example.pet;public class Cat extends Pet {    private double jumpHeight;    public Cat() {    }    public Cat(String name, int age, double weight) {        super(name, age, weight);    }    public void meow() {        System.out.println("Мяу-мяу!");    }    public void jump() {        System.out.println("Прыгает на " + jumpHeight + " метров!");    }}