package org.example.pet;/* 1. У нас в доме живут домашние животные. Попробуйте в этой задаче создать класс Petи записать ему в наследники Cat и Dog. Определите, какие характеристики и поведение ваших питомцевобщие, а какие проявляются только у конкретного типа. */public class Pet {    String name;    int age;    double weight;    public Pet() {    }    public Pet(String name, int age, double weight) {        this.name = name;        this.age = age;        this.weight = weight;    }    public void run() {        System.out.println(name + " бежит");    }    public void eats() {        System.out.println(name + " кушает");    }}